class Solution {
public:
    const int MOD = 1e9 + 7;
    int dp[2001][51][51]; 

    int f(int i, int a, int b, vector<int>& ar) {
        if (i == ar.size()) return 1;
        if (dp[i][a][b] != -1) return dp[i][a][b];

        int ans = 0;
        for (int j = 0; j <= 50; j++) {
            if (j >= a && ar[i] - j <= b && ar[i] - j >= 0) {
                ans = (ans + f(i + 1, j, ar[i] - j, ar)) % MOD;
            }
        }
        return dp[i][a][b] = ans;
    }

    int countOfPairs(vector<int>& ar) {
        memset(dp, -1, sizeof(dp)); 
        return f(0, 0, 50, ar);
    }
};
