static int MOD=1e9+7,memo[2002][52][52];
class Solution {
public:
    int dp(vector<int>& nums,int start,int prev1,int prev2) {
        if(start==nums.size())
            return 1;
        if(memo[start][prev1+1][prev2+1]!=-1)
            return memo[start][prev1+1][prev2+1];
        long result=0;
        for(int i=0;i<=nums[start];i++) {
            int first=i,second=nums[start]-i;
            if((first<=prev1||prev1==-1)&&(second>=prev2||prev2==-1))
                result=(result+dp(nums,start+1,first,second))%MOD;
        }
        return memo[start][prev1+1][prev2+1]=result;
    }
    int countOfPairs(vector<int>& nums) {
        memset(memo,-1,sizeof memo);
        return dp(nums,0,-1,-1);
    }
};
