class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10 ** 9 + 7
        n = len(nums)

        @functools.lru_cache(None)
        def dfs(i, inc, dec):
            if i == n:
                return 1

            if nums[i] < inc:
                return 0
            ans = 0
            for p1 in range(inc, nums[i]+1):
                p2 = nums[i] - p1
                if p2 <= dec:
                    ans += dfs(i+1, p1, p2)
            return ans % mod
        return dfs(0, 0, 50) % mod
