typedef long long ll;
const ll mod = 1e9 + 7;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        ll n = (ll) nums.size();
        vector<vector<ll>> dp(n, vector<ll>(55, -1));

        function<ll(ll, ll)> helper = [&] (ll pos, ll val) {
            if (pos >= n) return 1ll;
            if (dp[pos][val] != -1) return dp[pos][val];

            ll cur = nums[pos];
            ll prev = (pos > 0 ? nums[pos - 1] - val : 100);
            ll ans = 0;

            for (ll a1 = val; a1 <= cur; a1++) {
                ll a2 = cur - a1;
                if (prev < a2) continue;
                ans += helper(pos + 1, a1);
                ans %= mod;
            }

            dp[pos][val] = ans;
            return ans;
        };

        return helper(0, 0);
    }
};
