class Solution {
public:
    const int mod = 1e9+7;
    vector<int>a;
    int n, dp[2001][52][52];
    int cnt(int i, int l, int r){
        if(i==n) return 1;
        if(~dp[i][l][r]) return dp[i][l][r];
        int cur = 0;
        for(int j = 0;j<=a[i];j++){
            if(l==51){
                cur=(cur+cnt(i+1,j,a[i]-j))%mod;
            }
            else if(j>=l&&a[i]-j<=r){
                cur=(cur+cnt(i+1,j,a[i]-j))%mod;
            }
        }
        return dp[i][l][r] = cur;
    }
    int countOfPairs(vector<int>& aa) {
        memset(dp,-1,sizeof dp);
        a=aa;
        n = a.size();
        return cnt(0,51,51);
    }
};
