class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9 + 7
        n = len(nums)

        @lru_cache(None)
        def count_combinations(x_prev, y_prev, i):
            if i == n:
                return 1

            count = 0
            for x in range(x_prev, nums[i] + 1):
                y = nums[i] - x
                if y <= y_prev:
                    count += count_combinations(x, y, i + 1)
                    count %= mod
            return count

        return count_combinations(0, 50, 0) % mod
