class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        const int MOD = 1e9 + 7;

        vector<vector<int>> dp(n, vector<int>(51));

        // Base case: for the first element, all possible values of arr1[0] are valid
        for (int i = 0; i <= nums[0]; ++i) {
            dp[0][i] = 1;
        }

        for (int i = 1; i < n; ++i) {
            for (int j = 0; j <= nums[i]; ++j) {
                for (int k = 0; k <= j; ++k) {
                    if (nums[i] - j <= nums[i - 1] - k) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][k]) % MOD;
                    }
                }
            }
        }

        int count = 0;
        for (int j = 0; j <= nums[n - 1]; ++j) {
            count = (count + dp[n - 1][j]) % MOD;
        }

        return count;
    }
};
