class Solution {
public:
    int dp[2005][55],n,mod = (int)1e9+7;

    int solve(int ind,int val,int prev,vector<int>& nums) {
        if(ind >= n) return 1;
        if(dp[ind][val] != -1) return dp[ind][val];
        int res = 0;

        for(int i = val; i <= nums[ind]; i++) {
            int rem = nums[ind] - i;
            if(rem <= prev) res = (res + solve(ind+1,i,rem,nums))%mod;
        }
        return dp[ind][val] = res;
    }

    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        for(int i = 0; i <= n+1; i++) {
            for(int j = 0; j < 55; j++) dp[i][j] = -1;
        }
        return solve(0,0,50,nums);
    }
};
