class Solution {
public:
    int mod = 1e9+7;
    int f(int ind, int a, int b, vector<int> &v, vector<vector<vector<int>>> &dp)
    {
        if(ind==dp.size()) return 1;
        if(dp[ind][a][b] != -1 ) return dp[ind][a][b];
        int ret = 0;
        for(int x = a ; x <= v[ind] ; x++)
        {
            int y = v[ind]-x;
            if(y<=b)
            {
                ret  += f(ind+1, x,y, v, dp);
                ret %= mod;
            }
        }
        return dp[ind][a][b] = ret;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>> (52, vector<int> (52, -1)));
        return f(0, 0, 51, nums, dp);
    }
};
