class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int MOD = 1000000007;

        int[][][] dp = new int[n + 1][51][51];
        int[][] prefixSum = new int[51][51];

        for (int j = 0; j <= nums[0]; j++) {
            int k = nums[0] - j;
            dp[1][j][k] = 1;
        }

        for (int i = 2; i <= n; i++) {
            for (int j = 0; j <= 50; j++) {
                for (int k = 0; k <= 50; k++) {
                    prefixSum[j][k] = dp[i - 1][j][k];
                    if (j > 0) {
                        prefixSum[j][k] = (prefixSum[j][k] + prefixSum[j - 1][k]) % MOD;
                    }
                    if (k > 0) {
                        prefixSum[j][k] = (prefixSum[j][k] + prefixSum[j][k - 1]) % MOD;
                    }
                    if (j > 0 && k > 0) {
                        prefixSum[j][k] = (prefixSum[j][k] - prefixSum[j - 1][k - 1] + MOD) % MOD;
                    }
                }
            }

            for (int j = 0; j <= nums[i - 1]; j++) {
                int k = nums[i - 1] - j;

                dp[i][j][k] = prefixSum[j][50] - (k > 0 ? prefixSum[j][k - 1] : 0);
                dp[i][j][k] = (dp[i][j][k] + MOD) % MOD;
            }
        }

        int result = 0;
        for (int j = 0; j <= 50; j++) {
            for (int k = 0; k <= 50; k++) {
                result = (result + dp[n][j][k]) % MOD;
            }
        }

        return result;
    }
}
