#define ll long long int
ll dp[2001][52][52];
class Solution {
public:
    ll find(vector<int>& nums,ll n,ll ind,ll first,ll second){
        if(ind==n){
            return 1;
        }
        if(dp[ind][first][second]!=-1){
            return dp[ind][first][second];
        }
        ll ans=0;
        for(ll i=first;i<=nums[ind];i++){
            ll other=nums[ind]-i;
            if(second>=other){
                ans=(ans+find(nums,n,ind+1,i,other))%1000000007;
            }
        }
        return dp[ind][first][second]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        ll n=nums.size();

        for(ll i=0;i<n;i++){
            for(ll j=0;j<=51;j++){
                for(ll k=0;k<=51;k++){
                    dp[i][j][k]=-1;
                }
            }
        }
        ll ans=find(nums,n,0,0,51);
        return ans;
    }
};
