class Solution {
    const long long mod = 1e9+7;
    long long solve(int index, int prev, vector<int> &nums, int n, vector<vector<int>> &dp){
        if(index == n) return 1;
        if(dp[index][prev] != -1) return dp[index][prev];
        long long ans = 0;
        for(int i=prev; i<=nums[index]; i++){
            if(index == 0){
                ans = (ans + solve(index+1, i, nums, n, dp))%mod;
            }
            else{
                if(nums[index-1] - prev >= nums[index]-i){
                    ans = (ans + solve(index+1, i, nums, n, dp))%mod;
                }
            }
        }
        return dp[index][prev] = ans%mod;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(51, -1));
        return (int)solve(0, 0, nums, n, dp);
    }
};
