class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7

        @cache
        def dp(prevUp, i):
            # print(f'dp called on prevUp={prevUp}, i={i}')
            if i == len(nums):
                # print(f'base case, ret 1')
                return 1

            resThis = 0

            if prevUp > nums[i]:
                return 0

            prevDown = nums[i - 1] - prevUp if i else nums[0]

            for up in range(prevUp, nums[i] + 1):
                down = nums[i] - up
                if down > prevDown:
                    continue
                # print(f'trying new up: {up}')
                resThis += dp(up, i + 1)
            resThis %= MOD
            return resThis

        res = dp(0, 0)
        dp.cache_clear()
        return res
