class Solution {
public:
    int f[2020][55], g[2020][55];

    const int mod=1e9+7;

    int add(int x, int y){
        return (x+y)%mod;
    }

    int countOfPairs(vector<int>& w) {
        int n=w.size();
        for(int i=0; i<=w[0]; i++) f[0][i]=1, g[0][i]=(w[0]+1-i);

        for(int i=1; i<n; i++){
            for(int k=0; k<=w[i-1]; k++){
                int x=max(k, k+w[i]-w[i-1]);
                for(int j=x; j<=w[i]; j++) f[i][j]=add(f[i][j], f[i-1][k]);
            }
        }

        int res=0;
        for(int i=0; i<=w[n-1]; i++) res=add(res, f[n-1][i]);
        return res;
    }
};
