class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def dp(i, arr1, arr2):
            if i >= len(nums): return 1
            res = 0
            for n in range(nums[i] + 1):
                if n >= arr1 and nums[i] - n <= arr2:
                    res += dp(i + 1, n, nums[i] - n)
            return res % (10 ** 9 + 7)
        return dp(0, -float('inf'), float('inf'))
