class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int mod=1e9+7;
        vector<vector<long long>> dp(54,vector<long long>(54,0));
        for(int i=1;i<=52;i++)
        {
            for(int j=52;j>=1;j--)
            {
                if(i+j==nums[0]+2)
                    dp[i][j]=1ll;
            }
        }

        for(int a=1;a<nums.size();a++)
        {            
            for(int i=1;i<=52;i++)
            {
                for(int j=52;j>=1;j--)
                {
                    dp[i][j]=(dp[i-1][j]+dp[i][j+1]+mod-dp[i-1][j+1]+dp[i][j])%mod;
                }
            }

            for(int i=1;i<=52;i++)
            {
                for(int j=52;j>=1;j--)
                {
                    if(i+j!=nums[a]+2)
                        dp[i][j]=0;
                }
            }
        }

        long long ans=0ll;
        for(int i=1;i<=52;i++)
        {
            for(int j=1;j<=52;j++)
            {
                if(i+j==nums.back()+2)
                    ans=(ans+dp[i][j])%mod;
            }
        }

        return ans;
    }
};
