#define ll long long
class Solution {
public:
    ll MOD = 1e9 + 7;
    ll dp[2003][53][53];
    ll rec(int i, int l1, int l2, vector<int>& nums) {
        if (nums.size() == i) return 1;
        ll res = 0;
        if (dp[i][l1][l2] != -1) return dp[i][l1][l2];
        for (int j = l1; j <= nums[i]; j++) {
            int x = nums[i] - j;
            if (x > l2) continue;
            res = (res + rec(i + 1, j, x, nums)) % MOD;
        }
        return dp[i][l1][l2] = res % MOD;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp, -1, sizeof dp);
        return rec(0, 0, 50, nums);
    }
};
