#define ll long long
class Solution {
public:
const int M=1e9+7;
ll dp[2005][55][55];
ll func(int ind,int lasta,int lastb,vector<int> &v){
    if(ind<0) return 1;
    if(dp[ind][lasta][lastb]!=-1) return dp[ind][lasta][lastb];
    ll ans=0;
    for(int i=0;i<=v[ind];i++){
        int x=i,y=v[ind]-i;
        if(x<=lasta&&y>=lastb) ans=(ans+func(ind-1,x,y,v))%M;
    }
    return dp[ind][lasta][lastb]=ans;
}
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return func(nums.size()-1,51,0,nums);
    }
};
