class Solution {
public:
int n;
vector<int>v;
int dp[2005][55];
int mod=1e9+7;
int rec(int i,int a){
    if(i==n)return 1;
    if(dp[i][a]!=-1)return dp[i][a];
    int x=a;
    if(i==0)x=0;
    dp[i][a]=0;
    for(int j=x;j<=v[i];j++){
        if(i==0)(dp[i][a]+=rec(i+1,j))%=mod;
        else{
            if(v[i-1]-a>=v[i]-j)(dp[i][a]+=rec(i+1,j))%=mod;
        }
    }
    return dp[i][a];
}
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        v=nums;
        memset(dp,-1,sizeof(dp));
        return rec(0,0);
    }
};
