#include <vector>

class Solution {
public:
    const int mod = 1e9+7;
    int n;

    int solve(int ind, std::vector<int> &a, int l1, int l2, std::vector<std::vector<std::vector<int>>> &dp) {
        if (ind == n) return 1;  
        if (dp[ind][l1][l2] != -1) return dp[ind][l1][l2];

        int ans = 0;
        for (int arr1 = l1; arr1 <= a[ind]; ++arr1) { 
            int arr2 = a[ind] - arr1;
            if (arr2 <= l2) { 
                ans = (ans + solve(ind + 1, a, arr1, arr2, dp)) % mod;
            }
        }
        return dp[ind][l1][l2] = ans;
    }

    int countOfPairs(std::vector<int>& a) {
        n = a.size();
        std::vector<std::vector<std::vector<int>>> dp(n, std::vector<std::vector<int>>(51, std::vector<int>(51, -1)));
        return solve(0, a, 0, 50, dp);
    }
};
