const int mod=1e9+7;
typedef long long ll;
ll dp[2002][52];

class Solution {
public:


    ll solve(ll idx, ll pre1, ll pre2, vector<int> &nums){
        if(idx>=nums.size())return 1;
        if(dp[idx][pre1]!=-1)return dp[idx][pre1];
        ll res=0;
        for(ll cur1=pre1; cur1<=nums[idx]; cur1++){
            ll cur2=nums[idx]-cur1;
            if(pre2==-1 || cur2<=pre2){
                (res+=solve(idx+1, cur1, cur2, nums))%=mod;
            }
        }
        return dp[idx][pre1] = res;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return solve(0, 0, -1, nums);
    }
};
