class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10**9 + 7

        dp = [[0] * 51 for i in range(n)]

        for j in range(nums[0] + 1):
            dp[0][j] = 1

        for i in range(1, n):
            prefix_sum = 0
            for j in range(51):
                if j > nums[i]: break
                for k in range(j + 1):
                    if nums[i] - j <= nums[i-1] - k:
                        prefix_sum = (prefix_sum + dp[i-1][k]) % MOD

                dp[i][j] = prefix_sum
                prefix_sum = 0

        return sum(dp[n-1]) % MOD
