class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(2001, 0));


        for (int j = 0; j <= nums[0]; ++j) {
            dp[0][j] = 1;  
        }

        for (int i = 1; i < n; ++i) {
            vector<int> new_dp(2001, 0);

            for (int j = 0; j <= nums[i]; ++j) {
                int arr2_i = nums[i] - j; 
                for (int k = 0; k <= j; ++k) {  
                    if (arr2_i <= nums[i-1] - k) {  
                        new_dp[j] = (new_dp[j] + dp[i-1][k]) % MOD;
                    }
                }
            }
            dp[i] = new_dp;
        }

        int result = 0;
        for (int j = 0; j <= nums[n-1]; ++j) {
            result = (result + dp[n-1][j]) % MOD;
        }

        return result;
    }
};
