class Solution {
public:
    const int MOD = 1e9 + 7;

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<int>> dp(51, vector<int>(51, 0)); 
        for (int i = 0; i <= nums[0]; ++i) {
            dp[i][nums[0] - i] = 1;
        }


        for (int i = 1; i < n; ++i) {
            vector<vector<int>> new_dp(51, vector<int>(51, 0));
            for (int x = 0; x <= 50; ++x) {
                for (int y = 0; y <= 50; ++y) {
                    if (dp[x][y] == 0) continue;  

                    for (int new_x = x; new_x <= min(50, nums[i]); ++new_x) { 
                        int new_y = nums[i] - new_x;
                        if (new_y <= y) {  
                            new_dp[new_x][new_y] = (new_dp[new_x][new_y] + dp[x][y]) % MOD;
                        }
                    }
                }
            }
            dp = new_dp;
        }


        int result = 0;
        for (int x = 0; x <= 50; ++x) {
            for (int y = 0; y <= 50; ++y) {
                result = (result + dp[x][y]) % MOD;
            }
        }

        return result;
    }
};
