class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10 ** 9 + 7
        N = len(nums)
        memo = {}

        def solve(pos, n1, n2):
            if pos == N:
                return 1

            key = (pos, n1, n2)
            if key in memo:
                return memo[key]

            ans = 0
            for option in range(n1, nums[pos] + 1):
                if nums[pos] - option <= n2:
                    ans += solve(pos + 1, option, nums[pos] - option)
                    ans %= MOD

            memo[key] = ans
            return ans

        result = 0
        for i in range(0, nums[0] + 1):
            result += solve(1, i, nums[0] - i)
            result %= MOD

        return result
