class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10 ** 9 + 7
        @cache
        def helper(i, so_far_1, so_far_2):
            if i == len(nums):
                return 1
            output = 0
            for k in range(so_far_1, nums[i] + 1):
                if nums[i] - k <= so_far_2:
                    output = (output + helper(i + 1, k, nums[i] - k)) % MOD
            return output % MOD

        return helper(0, 0, 50) % MOD
