class Solution {
    #define ll long long 
    #define mod (ll)(1e9+7)
    ll dp[2000][51][51];
    ll helper(int idx,vector<int>&nums,int a,int b){
        if(idx==nums.size())return 1;

        if(dp[idx][a][b]!=-1)return dp[idx][a][b];
        ll ans=0;
        if(idx==0){
            for(int i=0;i<=nums[idx];i++){
                ans+=helper(idx+1,nums,i,nums[idx]-i);
                ans%=mod;
            }
        }
        else{
            for(int i=0;i<=nums[idx];i++){
                if(i>=a && nums[idx]-i<=b){
                    ans+=helper(idx+1,nums,i,nums[idx]-i);
                    ans%=mod;
                }
            }
        }
        return dp[idx][a][b]=ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        memset(dp,-1,sizeof(dp));
        return helper(0,nums,0,0);
    }
};
