#define ll long long
class Solution {
    ll f(ll ind, ll prev1, ll prev2, vector<int> &nums, vector<vector<vector<ll>>> &dp, ll &m){
        ll n = nums.size();
        if(ind >= n) return 1;
        ll ans = 0;
        if(dp[ind][prev1 + 1][prev2] != -1) return dp[ind][prev1 + 1][prev2];
        for(ll i = 0 ; i <= min(nums[ind], 50); i++){
            ll x = i, y = nums[ind] - x;
            if(x >= prev1 and y <= prev2){
                ans = (ans % m + f(ind + 1, x, y, nums, dp, m) % m) % m;
            }
        }
        return dp[ind][prev1 + 1][prev2] = ans % m;
    }
public:
    int countOfPairs(vector<int>& nums) {
        ll n = nums.size();
        ll m = 1e9 + 7;
        vector<vector<vector<ll>>> dp(n + 1, vector<vector<ll>> (54, vector<ll> (54, -1)));
        ll ans = f(0, -1, 52, nums, dp, m) % m;
        return ans; 
    }
};
