class Solution {
public:

    int dp[2001][55];
    int n;
    vector<int> v;
    const int mod = 1e9 + 7;

    int memo(int u, int last) {
        if (u == n) {
            return 1;
        }
        if (last != -1 && dp[u][last] != -1) return dp[u][last];

        int start;
        if (last == -1) start = 0;
        else start = last;

        int res = 0;

        for (int i = start; i <= v[u]; i++) {
            int other = v[u] - i;
            if (last != -1 && other > (v[u-1] - last)) continue;
            res += memo(u + 1, i);
            res %= mod;
        }
        if (last != -1) dp[u][last] = res;
        return res;

    }

    int countOfPairs(vector<int>& _v) {
        v = _v;
        n = v.size();
        int res = 0;
        for (int i = 0; i <=n; i++)
            for (int j = 0; j <= 50; j++)
                dp[i][j] = -1;
        res = memo(0, -1);
        return res;

    }
};
