class Solution {
public:
    int countOfPairs(vector<int>& a) {
    int n = size(a);
    int C = 1010;
    int BASE = 1e9 + 7;
    vector<int> f(C);
    for (int i = 0; i < C; i++)
    {
      if (i <= a[0])
        f[i] = 1;
      if (i)  
        f[i] += f[i - 1];
    }

    for (int i = 1; i < n; i++)
    {
      vector<int> newF(C);
      for (int j = 0; j <= a[i]; j++)
      {
        if (a[i] <= a[i - 1]) newF[j] = f[j];
        else if (j >= a[i] - a[i - 1]) newF[j] = f[j - (a[i] - a[i - 1])];

        if (j)
        {
          newF[j] += newF[j - 1];
          if (newF[j] >= BASE)
            newF[j] -= BASE;
        }
      }
      for (int j = a[i] + 1; j < C; j++)
        newF[j] = newF[j - 1];
      swap(f, newF);
    }

    return f[C - 1];
  }
};
