class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        mod = 10**9 + 7

        @cache
        def dp (idx,last):
            if idx == n: 
                return 1
            if last>nums[idx]:
                return 0 


            ans = 0 
            lastb = nums[idx-1]-last if idx>0 else mod

            for i in range(last,nums[idx]+1):
                y = nums[idx]-i

                if y<=lastb: 
                    ans += dp(idx+1,i)
                    ans %=mod

            return ans

        return dp(0,0)
