/**
 * @param {number[]} nums
 * @return {number}
 */
var countOfPairs = function(nums) {
  let n = nums.length, max = Math.max(...nums);
  let memo = Array(n).fill(0).map(() => Array(max + 1).fill(0).map(() => Array(max + 1).fill(-1)));
  let MOD = 1000000007;
  return dp(0, 0, max);

  function dp(i, lastOne, lastTwo) {
    if (i === n) return 1;
    if (memo[i][lastOne][lastTwo] !== -1) return memo[i][lastOne][lastTwo];

    let pairs = 0;
    for (let j = Math.max(lastOne, nums[i] - lastTwo); j <= nums[i]; j++) {
      // arr1 = increasing or equal
      // arr2 = decreasing or equal
      let dec = nums[i] - j;
      pairs = (pairs + dp(i + 1, j, nums[i] - j)) % MOD;
    }
    return memo[i][lastOne][lastTwo] = pairs;
  }
};
