class Solution {
public:
    const int MOD = 1e9 + 7;
    vector<vector<vector<int>>> memo;
    vector<int> nums;
    int n;

    int rec(int level, int prev1, int prev2){
        if(level >= n) return 1;
        if(memo[level][prev1][prev2] != -1){
          return memo[level][prev1][prev2];
        } 
        int ans = 0;
        for(int i = 0; i <= nums[level]; i++){
            if(prev1 <= i && prev2 >= nums[level]-i){
                ans = (ans + rec(level + 1, i, nums[level]-i)) % MOD;
            }
        }
        return memo[level][prev1][prev2] = ans;
    }

    int countOfPairs(vector<int>& arr) {
        n = arr.size();
        nums = arr;
        memo = vector<vector<vector<int>>>(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return rec(0, 0, 50);
    }
};
