#include <vector>
#include <iostream>
#include <algorithm>
#include <numeric>

using namespace std;

using ll = long long;

constexpr int MAX_VALUE = 50;
constexpr ll MOD_CONST = 1e9 + 7;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        ll modulo = MOD_CONST;
        int size = nums.size();

        vector<vector<ll>> memoTable(MAX_VALUE + 1, vector<ll>(MAX_VALUE + 1, 0));
        initializeTable(nums[0], memoTable);

        for (int index = 1; index < size; ++index) {
            vector<vector<ll>> newTable(MAX_VALUE + 1, vector<ll>(MAX_VALUE + 1, 0));
            updateTable(nums[index], memoTable, newTable, modulo);
            memoTable = move(newTable);
        }

        ll result = calculateResult(memoTable, modulo);

        return static_cast<int>(result);
    }

private:
    void initializeTable(int firstNum, vector<vector<ll>>& table) {
        for (int x = 0; x <= firstNum; ++x) {
            int y = firstNum - x;
            table[x][y]++;
        }
    }

    void updateTable(int currentNum, const vector<vector<ll>>& oldTable, vector<vector<ll>>& newTable, ll modulo) {
        for (int x1 = 0; x1 <= MAX_VALUE; ++x1) {
            for (int y1 = 0; y1 <= MAX_VALUE; ++y1) {
                if (oldTable[x1][y1] > 0) {
                    for (int x2 = x1; x2 <= currentNum; ++x2) {
                        int y2 = currentNum - x2;
                        if (y2 <= y1) {
                            newTable[x2][y2] = (newTable[x2][y2] + oldTable[x1][y1]) % modulo;
                        }
                    }
                }
            }
        }
    }

    ll calculateResult(const vector<vector<ll>>& table, ll modulo) {
        ll result = 0;
        for (int x = 0; x <= MAX_VALUE; ++x) {
            for (int y = 0; y <= MAX_VALUE; ++y) {
                result = (result + table[x][y]) % modulo;
            }
        }
        return result;
    }
};
