class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10 ** 9 + 7
        n = len(nums)

        @cache
        def dfs(idx, p1, p2):
            if idx == n:
                return 1

            x = nums[idx]
            cur = 0
            for i in range(x, -1, -1):
                c1, c2 = i, x - i
                if c1 >= p1 and c2 <= p2:
                    cur += dfs(idx + 1, c1, c2)
            return cur % MOD

        return dfs(0, 0, inf) % MOD
