class Solution {
public:

    vector<vector<long long>> dp;
    int n;
    long long mod = 1e9 + 7;
    long long memo(int i, int prev, vector<int> &nums){

        if(i== n){
            return 1;
        }

        long long &ans = dp[i][prev];
        if(ans!= -1){
            return ans;
        }

        ans = 0;

        if(i== 0){
            for(int j= 0;j<= nums[0]; j++){
                ans += memo(i+1, j, nums)%mod;
                ans %= mod;
            }
        }
        else{
            for(int j = prev; j<= nums[i]; j++){
                if(nums[i]- j<= nums[i-1]- prev){
                    ans += memo(i+1, j, nums)%mod;
                    ans %= mod;
                }
            }
        }

        return ans;
    }


    int countOfPairs(vector<int>& nums) {
         n = nums.size();

        dp.resize(n);
        for(int i= 0;i<n;i++){
            dp[i].resize(51);
        }

        for(int i= 0;i<n;i++){
            for(int j = 0;j< 51;j++){
                dp[i][j] = -1;
            }
        }


        long long ans= memo(0, 0, nums)%mod;

        return ans;

    }
};
