from typing import *

modulo = int(1e9 + 7)

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        dp = [[0] * 51 for i in range(n)]

        # step.1 init
        for i in range(nums[0] + 1):
            dp[0][i] = 1

        # step.2 dp
        for i in range(1, n):
            for current in range(nums[i] + 1):
                for previous in range(nums[i - 1] + 1):
                    prev_1, prev_2 = previous, nums[i - 1] - previous
                    cur_1, cur_2 = current, nums[i] - current
                    if prev_1 > cur_1 or prev_2 < cur_2:
                        continue
                    dp[i][current] = (dp[i][current] + dp[i - 1][previous]) % modulo

        # step.3 answer
        return sum(dp[-1]) % modulo
