class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        # a1 is increasing
        # a2 is decreasing
        # 
        mod = 10**9 + 7
        n = len(nums)
        # 2000
        # p1 is the previous element in a1 50
        # p2 is previous elmeent in a2 50
        @cache
        def rec(i, p1, p2):
            if i == n:
                return 1

            ans = 0
            for j in range(max(p1, 0), 51):
                other = nums[i] - j

                if 0 <= other <= p2:
                    ans = (ans + rec(i + 1, j, other)) % mod
            return ans

        return rec(0, -1, 51) % mod
