class Solution {
    int dp[][][]; int mod = (int)1e9+7;
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        dp = new int[n][55][55];
        for(int row[][]: dp){
            for(int col[]: row){
                Arrays.fill(col,-1);
            }
        }
        return helper(0,0,51,n,nums);        
    }

    int helper(int idx, int prev1, int prev2, int n, int nums[]){
        if(idx==n) return 1;
        if(dp[idx][prev1+1][prev2]!=-1) return dp[idx][prev1+1][prev2];
        long res = 0l;
        for(int e1 = prev1; e1<=nums[idx]; e1++){
            int e2 = nums[idx]-e1;
            if(e2>prev2 || e2<0) continue;
            res+=helper(idx+1,e1,e2,n,nums);
            res%=mod;
        }
        return dp[idx][prev1+1][prev2]=(int)res;
    }
}
