const int M=1e9+7;
class Solution {
public:

    int solveMem(vector<int>& nums,vector<vector<int>>&dp,int n,int idx,int prev,int p2){
        if(idx==n)
        return 1;

        if(dp[idx][prev]!=-1)
         return dp[idx][prev];

        int ans=0;
        for(int i=prev;i<=nums[idx];i++){
           if(nums[idx]-i<=p2) 
            ans=(ans%M+solveMem(nums,dp,n,idx+1,i,nums[idx]-i)%M)%M;
        }

        return dp[idx][prev]=ans;
    }


    int countOfPairs(vector<int>& nums) {

        int n=nums.size();
        vector<vector<int>>dp(n,vector<int>(51,-1));


        return solveMem(nums,dp,n,0,0,51);
    }
};
