class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        @cache
        def f(cur_val, cur_ind):
            if cur_ind == n:
                return 1

            prev = float('inf')
            if cur_ind > 0:
                prev = nums[cur_ind - 1] - cur_val

            p = 0
            for i in range(nums[cur_ind], cur_val-1, -1):
                if (nums[cur_ind] - i) > prev:
                    break
                p += f(i, cur_ind + 1)

            return p % MOD

        MOD = 10**9 + 7

        n = len(nums)

        return f(0, 0)
