class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod  = 10**9 + 7
        @lru_cache(None)
        def dfs(lp,hp,i):
            if i == len(nums):
                return 1
            val = nums[i]
            count  = 0
            for someVal in range(lp,val+1):
                if lp<=someVal and hp>=(val-someVal):
                    count+=dfs(someVal,val-someVal,i+1)
                    count%=mod
            return count%mod
        return dfs(0,inf,0)%mod
