class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n=len(nums)
        dp=[[[-1 for k in range(51)] for j in range(51)] for i in range(n)]
        mod=10**9+7
        def solve(i,a,b):
            if i==n: return 1
            if dp[i][a][b]!=-1: return dp[i][a][b]
            res=0
            for j in range(max(a,nums[i]-b),nums[i]+1): res=(res+solve(i+1,j,nums[i]-j))%mod
            dp[i][a][b]=res
            return res
        return solve(0,0,50)
