#include <vector>
using namespace std;

class Solution {
public:
    int helper(int idx,vector<int>& a,int prev1,int prev2,int mod, vector<vector<vector<int>>>& dp)
    {
        int n=a.size();
        if(idx>=n)
        {
            // cout<<"over"<<endl;
            return 1;
        }
        int ans=0;
        if( dp[idx][prev1+1][prev2+1]!=-1)
        {
            // cout<<idx<<" "<<prev1+1<<" "<<prev2+1<<endl;
            return  dp[idx][prev1+1][prev2+1];
        }
        // cout<<a[idx]<<endl;
        for(int i=0;i<=a[idx];i++)
        {
            // cout<<i<<" "<<a[idx]
            if( (i>=prev1) && ((a[idx]-i)<=prev2))
            {
                // cout<<i<<" "<<a[idx]-i<<endl;
                ans=(ans%mod+helper(idx+1,a,i,a[idx]-i,mod,dp)%mod)%mod;
            }
        }
        dp[idx][prev1+1][prev2+1]=ans%mod;
        return ans;
    }
    int countOfPairs(vector<int>& a) {
        int n=a.size();
        int mod=1e9+7;
        vector<vector<vector<int>>> dp(n+1,vector<vector<int>>(53,vector<int>(53,-1)));
        return helper(0,a,-1,51,mod,dp);

    }
};
