class Solution {
public:
    int dp[2001][51][51];
    int mod = 1e9 + 7;
        int sol(vector<int>&n,int i,int p1,int p2) {
            if (i >= n.size()) return 1;
            if (dp[i][p1][p2] != -1) return dp[i][p1][p2];
            int ans = 0;
            for (int j = 0; j <= n[i]; j++) {
                if (p1 <= j && p2 >= (n[i] - j)) 
                    ans = (ans + sol(n,i+1,j,n[i]-j)) % mod;
            }

            return dp[i][p1][p2] = ans;

        }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return sol(nums,0,0,nums[0]);
    }
};
