class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        @cache
        def recurse(idx,a,b):
            if idx==len(nums):
                return 1

            res = 0
            curr = nums[idx]
            for na in itertools.count(a):
                nb = curr-na
                if nb > b:
                    continue
                if nb < 0:
                    break
                res += recurse(idx+1, na, nb)%(7+10**9)
            return res


        res = recurse(0,0,nums[0])%(7+10**9)
        recurse.cache_clear()
        return res
