MOD = 10**9 + 7

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        max_num = 50

        # dp[i][j] will store the number of valid sequences up to index i with arr1[i] = j
        dp = [[0] * (max_num + 1) for _ in range(n)]

        # Base case: Initialize dp for the first element
        for j in range(nums[0] + 1):
            dp[0][j] = 1

        # Fill dp table
        for i in range(1, n):
            for j in range(nums[i] + 1):
                arr2_i = nums[i] - j
                if 0 <= arr2_i <= max_num:
                    dp[i][j] = sum(dp[i-1][k] for k in range(j + 1) if nums[i-1] - k >= arr2_i) % MOD

        # Sum up the possible sequences for the last index
        result = sum(dp[n-1][j] for j in range(nums[-1] + 1)) % MOD

        return result
