class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        const int MOD = 1e9 + 7;
        vector<vector<long long>> dp(51, vector<long long>(51, 0));

        for (int i = 0; i <= nums[0]; i++) {
            dp[i][nums[0] - i] = 1;
        }

        for (int i = 1; i < n; i++) {
            vector<vector<long long>> new_dp(51, vector<long long>(51, 0));

            for (int j = 0; j <= 50; j++) {
                for (int k = 0; k <= 50; k++) {
                    if (dp[j][k] == 0) continue;

                    for (int x = j; x <= nums[i]; x++) {
                        int y = nums[i] - x;
                        if (y <= k) {
                            new_dp[x][y] = (new_dp[x][y] + dp[j][k]) % MOD;
                        }
                    }
                }
            }

            dp = new_dp;
        }

        long long result = 0;
        for (int i = 0; i <= 50; i++) {
            for (int j = 0; j <= 50; j++) {
                result = (result + dp[i][j]) % MOD;
            }
        }

        return result;
    }
};
