class Solution {
public:
    int dp[2001][52][52];
    int MOD = 1e9+7;
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return helper(nums, 0, 0, 51);
    }

    int helper(vector<int> &nums, int idx, int preva, int prevb){
        if( idx >= nums.size() ){
            return 1;
        } else if( dp[idx][preva][prevb] == -1 ){
            int ways = 0;
            for(int a=nums[idx]; a>=0; a--){
                int b = nums[idx]-a;
                if( a >= preva && b <= prevb ){
                    ways = (ways + helper(nums, idx+1, a, b))%MOD;
                }
            }
            dp[idx][preva][prevb] = ways%MOD;
        }
        return dp[idx][preva][prevb];
    }
};
