class Solution {
public:
    int mod=1e9+7;
    long long cop(vector<int>&nums,vector<vector<long long> >&dp,int i,int prev){
        if(i==nums.size()){
            return 1;
        }
        if(dp[i][prev+1]!=-1){
            return dp[i][prev+1]%mod;

        }
        long long ans=0;
        if(prev==-1){

            for(int k=0;k<=nums[i];k++){
                ans=(ans%mod+cop(nums,dp,i+1,k)%mod)%mod;

            }


        } else{
            for(int k=prev;k<=nums[i];k++){
                if((nums[i-1]-prev)>=(nums[i]-k)){
                    ans=(ans%mod+cop(nums,dp,i+1,k)%mod)%mod;
                }
            }

        }
        return dp[i][prev+1]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<long long> >dp(nums.size(),vector<long long>(52,-1));
        return cop(nums,dp,0,-1)%mod;


    }
};
